create or replace database QCMPOC;
#------------------------------------------------------------
# Table: user
#------------------------------------------------------------
use QCMPOC;

CREATE or replace TABLE user(
        idUser int (11) Auto_increment  NOT NULL ,
        nom    Varchar (30) ,
        prenom Varchar (30) ,
        PRIMARY KEY (idUser )
)ENGINE=InnoDB;


#------------------------------------------------------------
# Table: topic
#------------------------------------------------------------

CREATE or replace TABLE topic(
        idTopic          int (11) Auto_increment  NOT NULL ,
        nameTopic        Varchar (100) ,
        descriptionTopic Varchar (1000) ,
        PRIMARY KEY (idTopic )
)ENGINE=InnoDB;


#------------------------------------------------------------
# Table: questions
#------------------------------------------------------------

CREATE or replace TABLE questions(
        idQuestion   int (11) Auto_increment  NOT NULL ,
        question_nlg Varchar (1000) ,
        good_answer  Varchar (1000) ,
        distractor_1 Varchar (1000) ,
        distractor_2 Varchar (1000) ,
        distractor_3 Varchar (1000) ,
        idTopic      Int NOT NULL ,
        PRIMARY KEY (idQuestion)
)ENGINE=InnoDB;


#------------------------------------------------------------
# Table: score
#------------------------------------------------------------

CREATE or replace TABLE score(
        score       Int ,
        idUser      Int NOT NULL ,
        idTopic     Int NOT NULL ,
        PRIMARY KEY (idUser ,idTopic )
)ENGINE=InnoDB;

ALTER TABLE questions ADD CONSTRAINT FK_questions_idTopic FOREIGN KEY (idTopic) REFERENCES topic(idTopic);
ALTER TABLE score ADD CONSTRAINT FK_score_idUser FOREIGN KEY (idUser) REFERENCES user(idUser);
ALTER TABLE score ADD CONSTRAINT FK_score_idTopic FOREIGN KEY (idTopic) REFERENCES topic(idTopic);


